mapper.select:
SELECT post_no, name, is_admin, contents, create_time, views, category_id, user_no, file_id, update_time FROM t_post WHERE 1=1
AND post_no = #{postNo}
AND name = #{name}
AND create_time LIKE CONCAT('%', #{createTime}, '%')

mapper.delete:
DELETE FROM t_post
WHERE post_no = #{postNo}
    AND user_no = #{userNo}
